const (
	envProxyLBRealServerIP0 = "SAKURACLOUD_PROXYLB_SERVER0"
	envProxyLBRealServerIP1 = "SAKURACLOUD_PROXYLB_SERVER1"
)

var proxyLBRealServerIP0, proxyLBRealServerIP1 string

func TestAccResourceSakuraCloudProxyLB(t *testing.T) {

	if ip, ok := os.LookupEnv(envProxyLBRealServerIP0); ok {
		proxyLBRealServerIP0 = ip
	} else {
		t.Skipf("ENV %q is requilred. skip", envProxyLBRealServerIP0)
		return
	}

	var proxylb sacloud.ProxyLB
	resource.ParallelTest(t, resource.TestCase{
		PreCheck:     func() { testAccPreCheck(t) },
		Providers:    testAccProviders,
		CheckDestroy: testAccCheckSakuraCloudProxyLBDestroy,
		Steps: []resource.TestStep{
			{
				Config: fmt.Sprintf(testAccCheckSakuraCloudProxyLBConfig_basic, proxyLBRealServerIP0),
				Check: resource.ComposeTestCheckFunc(
					testAccCheckSakuraCloudProxyLBExists("sakuracloud_proxylb.foobar", &proxylb),
					resource.TestCheckResourceAttr(
						"sakuracloud_proxylb.foobar", "name", "terraform-test-proxylb"),
					resource.TestCheckResourceAttr(
						"sakuracloud_proxylb.foobar", "plan", "5000"),
					resource.TestMatchResourceAttr(
						"sakuracloud_proxylb.foobar", "fqdn", regexp.MustCompile(`.+\.sakura\.ne\.jp$`)),
					resource.TestCheckResourceAttr(
						"sakuracloud_proxylb.foobar", "vip_failover", "true"),
					resource.TestCheckResourceAttr(
						"sakuracloud_proxylb.foobar", "sticky_session", "true"),
					resource.TestCheckResourceAttr(
						"sakuracloud_proxylb.foobar", "health_check.0.protocol", "http"),
					resource.TestCheckResourceAttr(
						"sakuracloud_proxylb.foobar", "health_check.0.delay_loop", "10"),
					resource.TestCheckResourceAttr(
						"sakuracloud_proxylb.foobar", "health_check.0.host_header", "usacloud.jp"),
					resource.TestCheckResourceAttr(
						"sakuracloud_proxylb.foobar", "health_check.0.path", "/"),
					resource.TestCheckResourceAttr(
						"sakuracloud_proxylb.foobar", "sorry_server.0.ipaddress", proxyLBRealServerIP0),
					resource.TestCheckResourceAttr(
						"sakuracloud_proxylb.foobar", "sorry_server.0.port", "80"),
					resource.TestCheckResourceAttr(
						"sakuracloud_proxylb.foobar", "bind_ports.0.proxy_mode", "http"),
					resource.TestCheckResourceAttr(
						"sakuracloud_proxylb.foobar", "bind_ports.0.port", "80"),
					resource.TestCheckResourceAttr(
						"sakuracloud_proxylb.foobar", "bind_ports.0.response_header.#", "1"),
					resource.TestCheckResourceAttr(
						"sakuracloud_proxylb.foobar", "bind_ports.0.response_header.0.header", "Cache-Control"),
					resource.TestCheckResourceAttr(
						"sakuracloud_proxylb.foobar", "bind_ports.0.response_header.0.value", "public, max-age=10"),
					resource.TestCheckResourceAttrPair(
						"sakuracloud_proxylb.foobar", "servers.0.ipaddress",
						"sakuracloud_server.server01", "ipaddress",
					),
					resource.TestCheckResourceAttr(
						"sakuracloud_proxylb.foobar", "servers.0.port", "80"),
					resource.TestCheckResourceAttr(
						"sakuracloud_proxylb.foobar", "servers.0.enabled", "true"),
					resource.TestCheckResourceAttrPair(
						"sakuracloud_proxylb.foobar", "icon_id",
						"sakuracloud_icon.foobar", "id",
					),
				),
			},
			{
				Config: testAccCheckSakuraCloudProxyLBConfig_update,
				Check: resource.ComposeTestCheckFunc(
					testAccCheckSakuraCloudProxyLBExists("sakuracloud_proxylb.foobar", &proxylb),
					resource.TestCheckResourceAttr(
						"sakuracloud_proxylb.foobar", "name", "terraform-test-proxylb-upd"),
					resource.TestCheckResourceAttr(
						"sakuracloud_proxylb.foobar", "sticky_session", "false"),
					resource.TestCheckResourceAttr(
						"sakuracloud_proxylb.foobar", "health_check.0.protocol", "tcp"),
					resource.TestCheckResourceAttr(
						"sakuracloud_proxylb.foobar", "health_check.0.delay_loop", "20"),
					resource.TestCheckResourceAttr(
						"sakuracloud_proxylb.foobar", "health_check.0.host_header", ""),
					resource.TestCheckResourceAttr(
						"sakuracloud_proxylb.foobar", "health_check.0.path", ""),
					resource.TestCheckNoResourceAttr("sakuracloud_proxylb.foobar", "sorry_server.0.ipaddress.#"),
					resource.TestCheckResourceAttr(
						"sakuracloud_proxylb.foobar", "bind_ports.0.proxy_mode", "https"),
					resource.TestCheckResourceAttr(
						"sakuracloud_proxylb.foobar", "bind_ports.0.port", "443"),
					resource.TestCheckResourceAttr(
						"sakuracloud_proxylb.foobar", "bind_ports.0.response_header.#", "0"),
					resource.TestCheckResourceAttrPair(
						"sakuracloud_proxylb.foobar", "servers.0.ipaddress",
						"sakuracloud_server.server01", "ipaddress",
					),
					resource.TestCheckResourceAttr(
						"sakuracloud_proxylb.foobar", "servers.0.port", "443"),
					resource.TestCheckResourceAttr(
						"sakuracloud_proxylb.foobar", "servers.0.enabled", "true"),
				),
			},
		},
	})
}

func testAccCheckSakuraCloudProxyLBExists(n string, proxylb *sacloud.ProxyLB) resource.TestCheckFunc {
	return func(s *terraform.State) error {
		rs, ok := s.RootModule().Resources[n]

		if !ok {
			return fmt.Errorf("not found: %s", n)
		}

		if rs.Primary.ID == "" {
			return errors.New("no ProxyLB ID is set")
		}

		client := testAccProvider.Meta().(*APIClient)
		proxyLBOp := sacloud.NewProxyLBOp(client)

		foundProxyLB, err := proxyLBOp.Read(context.Background(), types.StringID(rs.Primary.ID))
		if err != nil {
			return err
		}

		if foundProxyLB.ID.String() != rs.Primary.ID {
			return fmt.Errorf("not found ProxyLB: %s", rs.Primary.ID)
		}
		*proxylb = *foundProxyLB
		return nil
	}
}

func testAccCheckSakuraCloudProxyLBDestroy(s *terraform.State) error {
	client := testAccProvider.Meta().(*APIClient)
	proxyLBOp := sacloud.NewProxyLBOp(client)

	for _, rs := range s.RootModule().Resources {
		if rs.Type != "sakuracloud_proxylb" {
			continue
		}

		_, err := proxyLBOp.Read(context.Background(), types.StringID(rs.Primary.ID))
		if err == nil {
			return fmt.Errorf("still exists ProxyLB: %s", rs.Primary.ID)
		}
	}

	return nil
}

func TestAccImportSakuraCloudProxyLB(t *testing.T) {

	if ip, ok := os.LookupEnv(envProxyLBRealServerIP0); ok {
		proxyLBRealServerIP0 = ip
	} else {
		t.Skipf("ENV %q is requilred. skip", envProxyLBRealServerIP0)
		return
	}
	if ip, ok := os.LookupEnv(envProxyLBRealServerIP1); ok {
		proxyLBRealServerIP1 = ip
	} else {
		t.Skipf("ENV %q is requilred. skip", envProxyLBRealServerIP1)
		return
	}

	checkFn := func(s []*terraform.InstanceState) error {
		if len(s) != 1 {
			return fmt.Errorf("expected 1 state: %#v", s)
		}
		expects := map[string]string{
			"name":                      "terraform-test-proxylb-import",
			"vip_failover":              "true",
			"sticky_session":            "true",
			"timeout":                   "10",
			"region":                    "is1",
			"health_check.0.protocol":   "tcp",
			"health_check.0.delay_loop": "20",
			"description":               "ProxyLB from TerraForm for SAKURA CLOUD",
			"tags.0":                    "hoge1",
			"tags.1":                    "hoge2",
			"bind_ports.0.proxy_mode":   "https",
			"bind_ports.0.port":         "443",
			"servers.#":                 "2",
			"servers.0.ipaddress":       proxyLBRealServerIP0,
			"servers.0.port":            "80",
			"servers.0.enabled":         "true",
			"servers.1.ipaddress":       proxyLBRealServerIP1,
			"servers.1.port":            "80",
			"servers.1.enabled":         "true",
		}

		if err := compareStateMulti(s[0], expects); err != nil {
			return err
		}
		return stateNotEmptyMulti(s[0], "fqdn", "proxy_networks.0")
	}

	resourceName := "sakuracloud_proxylb.foobar"

	resource.ParallelTest(t, resource.TestCase{
		PreCheck:     func() { testAccPreCheck(t) },
		Providers:    testAccProviders,
		CheckDestroy: testAccCheckSakuraCloudProxyLBDestroy,
		Steps: []resource.TestStep{
			{
				Config: fmt.Sprintf(testAccCheckSakuraCloudProxyLBConfig_import, proxyLBRealServerIP0, proxyLBRealServerIP1),
			},
			{
				ResourceName:      resourceName,
				ImportState:       true,
				ImportStateCheck:  checkFn,
				ImportStateVerify: true,
			},
		},
	})
}

