func TestAccSakuraCloudSimpleMonitor_basic(t *testing.T) {
	resourceName := "sakuracloud_simple_monitor.foobar"
	zone := randomName() + ".com"

	var monitor iaas.SimpleMonitor
	resource.ParallelTest(t, resource.TestCase{
		PreCheck:          func() { testAccPreCheck(t) },
		ProviderFactories: testAccProviderFactories,
		CheckDestroy:      testCheckSakuraCloudSimpleMonitorDestroy,
		Steps: []resource.TestStep{
			{
				Config: buildConfigWithArgs(testAccSakuraCloudSimpleMonitor_basic, zone, testAccSlackWebhook),
				Check: resource.ComposeTestCheckFunc(
					testCheckSakuraCloudSimpleMonitorExists(resourceName, &monitor),
					testCheckSakuraCloudSimpleMonitorAttributes(&monitor),
					resource.TestCheckResourceAttr(resourceName, "delay_loop", "60"),
					resource.TestCheckResourceAttr(resourceName, "timeout", "20"),
					resource.TestCheckResourceAttr(resourceName, "health_check.0.protocol", "https"),
					resource.TestCheckResourceAttr(resourceName, "health_check.0.port", "8443"),
					resource.TestCheckResourceAttr(resourceName, "health_check.0.contains_string", "ok"),
					resource.TestCheckResourceAttr(resourceName, "health_check.0.sni", "true"),
					resource.TestCheckResourceAttr(resourceName, "health_check.0.verify_sni", "true"),
					resource.TestCheckResourceAttr(resourceName, "health_check.0.username", "foo"),
					resource.TestCheckResourceAttr(resourceName, "health_check.0.password", "bar"),
					resource.TestCheckResourceAttr(resourceName, "health_check.0.http2", "true"),
					resource.TestCheckResourceAttr(resourceName, "health_check.0.contains_string", "ok"),
					resource.TestCheckResourceAttr(resourceName, "target", zone),
					resource.TestCheckResourceAttr(resourceName, "notify_email_enabled", "true"),
					resource.TestCheckResourceAttr(resourceName, "notify_email_html", "true"),
					resource.TestCheckResourceAttr(resourceName, "notify_slack_enabled", "true"),
					resource.TestCheckResourceAttr(resourceName, "notify_slack_webhook", testAccSlackWebhook),
					resource.TestCheckResourceAttrPair(
						resourceName, "icon_id",
						"sakuracloud_icon.foobar", "id",
					),
				),
			},
			{
				Config: buildConfigWithArgs(testAccSakuraCloudSimpleMonitor_update, zone, testAccSlackWebhook),
				Check: resource.ComposeTestCheckFunc(
					testCheckSakuraCloudSimpleMonitorExists(resourceName, &monitor),
					testCheckSakuraCloudSimpleMonitorAttributesUpdated(&monitor),
					resource.TestCheckResourceAttr(resourceName, "max_check_attempts", "5"),
					resource.TestCheckResourceAttr(resourceName, "retry_interval", "20"),
					resource.TestCheckResourceAttr(resourceName, "timeout", "30"),
					resource.TestCheckResourceAttr(resourceName, "health_check.0.protocol", "http"),
					resource.TestCheckResourceAttr(resourceName, "health_check.0.port", "80"),
					resource.TestCheckResourceAttr(resourceName, "health_check.0.host_header", "usacloud.jp"),
					resource.TestCheckResourceAttr(resourceName, "health_check.0.http2", "false"),
					resource.TestCheckResourceAttr(resourceName, "health_check.0.contains_string", ""),
					resource.TestCheckResourceAttr(resourceName, "target", zone),
					resource.TestCheckResourceAttr(resourceName, "notify_email_enabled", "false"),
					resource.TestCheckResourceAttr(resourceName, "notify_email_html", "false"),
					resource.TestCheckResourceAttr(resourceName, "notify_email_enabled", "false"),
					resource.TestCheckResourceAttr(resourceName, "icon_id", ""),
				),
			},
		},
	})
}

func TestAccSakuraCloudSimpleMonitor_certMonitor(t *testing.T) {
	resourceName := "sakuracloud_simple_monitor.foobar"
	zone := randomName() + ".com"

	var monitor iaas.SimpleMonitor
	resource.ParallelTest(t, resource.TestCase{
		PreCheck:          func() { testAccPreCheck(t) },
		ProviderFactories: testAccProviderFactories,
		CheckDestroy:      testCheckSakuraCloudSimpleMonitorDestroy,
		Steps: []resource.TestStep{
			{
				Config: buildConfigWithArgs(testAccSakuraCloudSimpleMonitor_certMonitor, zone, testAccSlackWebhook),
				Check: resource.ComposeTestCheckFunc(
					testCheckSakuraCloudSimpleMonitorExists(resourceName, &monitor),
					testCheckSakuraCloudSimpleMonitorAttributes(&monitor),
					resource.TestCheckResourceAttr(resourceName, "health_check.0.protocol", "sslcertificate"),
					resource.TestCheckResourceAttr(resourceName, "health_check.0.remaining_days", "30"),
					resource.TestCheckResourceAttr(resourceName, "target", zone),
					resource.TestCheckResourceAttr(resourceName, "notify_email_enabled", "true"),
					resource.TestCheckResourceAttr(resourceName, "notify_email_html", "true"),
					resource.TestCheckResourceAttr(resourceName, "notify_slack_enabled", "true"),
					resource.TestCheckResourceAttr(resourceName, "notify_slack_webhook", testAccSlackWebhook),
					resource.TestCheckResourceAttrPair(
						resourceName, "icon_id",
						"sakuracloud_icon.foobar", "id",
					),
				),
			},
		},
	})
}

func testCheckSakuraCloudSimpleMonitorExists(n string, monitor *iaas.SimpleMonitor) resource.TestCheckFunc {
	return func(s *terraform.State) error {
		rs, ok := s.RootModule().Resources[n]

		if !ok {
			return fmt.Errorf("not found: %s", n)
		}

		if rs.Primary.ID == "" {
			return errors.New("no SimpleMonitor ID is set")
		}

		client := testAccProvider.Meta().(*APIClient)
		smOp := iaas.NewSimpleMonitorOp(client)

		foundSimpleMonitor, err := smOp.Read(context.Background(), sakuraCloudID(rs.Primary.ID))
		if err != nil {
			return err
		}

		if foundSimpleMonitor.ID.String() != rs.Primary.ID {
			return fmt.Errorf("not found SimpleMonitor: %s", rs.Primary.ID)
		}

		*monitor = *foundSimpleMonitor
		return nil
	}
}

func testCheckSakuraCloudSimpleMonitorAttributes(monitor *iaas.SimpleMonitor) resource.TestCheckFunc {
	return func(s *terraform.State) error {
		if monitor.DelayLoop != 60 {
			return fmt.Errorf("bad delay_loop: expected:60 got:%d", monitor.DelayLoop)
		}
		return nil
	}
}

func testCheckSakuraCloudSimpleMonitorAttributesUpdated(monitor *iaas.SimpleMonitor) resource.TestCheckFunc {
	return func(s *terraform.State) error {
		if monitor.DelayLoop != 120 {
			return fmt.Errorf("bad delay_loop: expected:120 got:%d", monitor.DelayLoop)
		}
		return nil
	}
}

func testCheckSakuraCloudSimpleMonitorDestroy(s *terraform.State) error {
	client := testAccProvider.Meta().(*APIClient)
	smOp := iaas.NewSimpleMonitorOp(client)

	for _, rs := range s.RootModule().Resources {
		if rs.Type != "sakuracloud_simple_monitor" {
			continue
		}
		if rs.Primary.ID == "" {
			continue
		}

		_, err := smOp.Read(context.Background(), sakuraCloudID(rs.Primary.ID))
		if err == nil {
			return fmt.Errorf("still exists SimpleMonitor: %s", rs.Primary.ID)
		}
	}
	return nil
}

